import RoundImage from "react-rounded-image";
import { colors } from "../../utils/Themes/values";
import { constants } from "../../utils/constants";
import classNames from "classnames";

const HomePage = props => {
  const { classes } = props;
  const email = constants.CONTACT[0];
  const mobileWidth = 100;

  const [windowWidth, setWindowWidth] = React.useState(window.innerWidth);

  useEffect(() => {
    window.addEventListener('resize', handleWindowSizeChange);

    return function cleanup() {
      window.removeEventListener('resize', handleWindowSizeChange);
    }
  });

  const handleWindowSizeChange = useCallback(() => {
    setWindowWidth({ width: window.innerWidth });
  }, []);

  return (
    <div className={"page"}
         style={windowWidth > mobileWidth ? null : {overflow: "auto"}}
    >
      <div className={classes.header}>
        <h1 className={classes.title}>Hello World!</h1>
        <div className={classes.bottomLine}/>
      </div>

      <div style={{marginTop: "5%"}}>
        <div className={classNames({
          [classes.gridItemMobile]: (windowWidth <= mobileWidth),
          [classes.gridItemNotMobile]: (windowWidth > mobileWidth)
        })}
        >
          <div style={{marginLeft: "auto", marginRight: "auto", width: "250px"}}>
            <RoundImage
                image={"/imgs/profile2.jpg"}
                roundedColor={colors.CHAPMAN_RED}
                imageWidth={windowWidth > mobileWidth ? "500" : "250"}
                imageHeight={windowWidth > mobileWidth ? "500" : "250"}
                roundedSize="6"
            />
