import React, {useCallback, useEffect} from 'react';
import {constants} from "../../utils/constants";
import useStyles from "./NavTabs.styles";
import {CssBaseline, AppBar, Tabs, Tab, Toolbar, IconButton} from "@material-ui/core";
import MenuIcon from '@material-ui/icons/Menu';

function a11yProps(index) {
    return {
        id: `nav-tab-${index}`,
        'aria-controls': `nav-tabpanel-${index}`,
    };
}

function LinkTab(props) {
    return (
            <Tab
                component="a"
                {...props}
            />
    );
}

const NavTabs = props => {
    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const mobileWidth = constants.GLOBAL_VARS.windowSize;
    const [isMobile, setIsMobile] = React.useState(window.innerWidth <= mobileWidth);

    useEffect(() => {
        window.addEventListener('resize', handleWindowSizeChange);

        return function cleanup() {
            window.removeEventListener('resize', handleWindowSizeChange);
        }
    });

    const handleWindowSizeChange = useCallback(() => {
        window.innerWidth > mobileWidth ? setIsMobile(false) : setIsMobile(true);
        console.log(window.innerWidth);
    }, []);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
        isMobile
            ? (<div>
                <CssBaseline />
                    <AppBar>
                        <Toolbar>
                            <IconButton>
                                <MenuIcon />
                            </IconButton>

                        </Toolbar>
                    </AppBar>
                </div>
            )
            : (<Tabs
                variant="fullWidth"
                value={value}
                onChange={handleChange}
                classes={{
                    root: classes.root,
                    flexContainer: classes.flex,
                }}
                centered
                >
                    {constants.MENU.map(tab => (
                        <LinkTab
                            key={tab.id}
                            style={{color: "lightgrey", textDecoration: "none"}}
                            label={<span className={classes.label}>{tab.title}</span>}
                            href={(tab.title === "Resume" ? "" : "/#") +
                            tab.url}
                            {...a11yProps(tab.id)}

                        />
                    ))}
                </Tabs>)
    );
};

export default NavTabs;
